// src/pages/RegisterPage.js
import React, { useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { useToast } from '../hooks/useToast';
import AuthCard from '../components/auth/AuthCard';

// Definisikan komponen Spinner di sini atau import dari file terpisah
const Spinner = () => <div className="spinner"></div>;

function RegisterPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false); // State untuk toggle password
  const [passwordError, setPasswordError] = useState(''); // State untuk error real-time
  const [isLoading, setIsLoading] = useState(false);
  const { register, token } = useAuth();
  const { showToast } = useToast();

  // Handler untuk validasi password real-time
  const handlePasswordChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);

    if (newPassword.length > 0 && newPassword.length < 6) {
      setPasswordError('Password minimal 6 karakter');
    } else {
      setPasswordError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validasi ulang saat submit
    if (password.length < 6) {
      showToast('Password minimal 6 karakter', 'error');
      setPasswordError('Password minimal 6 karakter'); // Pastikan pesan error terlihat
      return;
    }

    // Hapus error jika sudah valid
    setPasswordError('');

    setIsLoading(true);
    await register(name, email, password);
    setIsLoading(false);
  };

  if (token) {
    return <Navigate to="/dashboard" />;
  }

  return (
    <AuthCard subtitle="Buat akun baru">
      <form id="registerForm" onSubmit={handleSubmit}>

        {/* Form Group Nama */}
        <div className="form-group">
          <input 
            type="text" 
            id="name" 
            className="form-input" 
            placeholder=" "
            value={name}
            onChange={(e) => setName(e.target.value)}
            required 
          />
          <label htmlFor="name" className="form-label">Nama Lengkap</label>
        </div>

        {/* Form Group Email */}
        <div className="form-group">
          <input 
            type="email" 
            id="email" 
            className="form-input" 
            placeholder=" "
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
          <label htmlFor="email" className="form-label">Email</label>
        </div>

        {/* Form Group Password */}
        <div className="form-group">
          <input 
            type={showPassword ? 'text' : 'password'}
            id="password" 
            className="form-input input-password" // Tambah class input-password
            placeholder=" " 
            value={password}
            onChange={handlePasswordChange} // Ganti ke handler validasi
            required 
          />
          <label htmlFor="password" className="form-label">Password</label>
          <button 
            type="button" 
            className="password-toggle-btn"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? 'Hide' : 'Show'}
          </button>

          {/* Tampilkan pesan error real-time */}
          {passwordError && <small className="form-error">{passwordError}</small>}
        </div>

        {/* Tombol Submit dengan Spinner */}
        <button type="submit" className="btn" disabled={isLoading}>
          {isLoading ? <Spinner /> : 'Daftar'}
        </button>
      </form>

      <div className="auth-links text-center">
        <span>Sudah punya akun? <Link to="/login" className="auth-link">Login di sini</Link></span>
      </div>
    </AuthCard>
  );
}

export default RegisterPage;
