// src/pages/LoginPage.js
import React, { useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import AuthCard from '../components/auth/AuthCard';

// Definisikan komponen Spinner di sini atau import dari file terpisah
const Spinner = () => <div className="spinner"></div>;

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false); // State untuk toggle password
  const [isLoading, setIsLoading] = useState(false);
  const { login, token } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    await login(email, password);
    setIsLoading(false);
  };

  // Jika sudah login, redirect ke dashboard
  if (token) {
    return <Navigate to="/dashboard" />;
  }

  return (
    <AuthCard subtitle="Login ke akun Anda">
      <form id="loginForm" onSubmit={handleSubmit}>

        {/* Form Group Email dengan Floating Label */}
        <div className="form-group">
          <input 
            type="email" 
            id="email" 
            className="form-input" 
            placeholder=" " /* <-- Wajib ada spasi untuk :not(:placeholder-shown) */
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
          <label htmlFor="email" className="form-label">Email</label>
        </div>

        {/* Form Group Password dengan Floating Label dan Toggle */}
        <div className="form-group">
          <input 
            type={showPassword ? 'text' : 'password'} 
            id="password" 
            className="form-input input-password" /* Tambah class input-password */
            placeholder=" " /* <-- Wajib ada spasi */
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required 
          />
          <label htmlFor="password" className="form-label">Password</label>
          <button 
            type="button" 
            className="password-toggle-btn"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? 'Hide' : 'Show'}
          </button>
        </div>

        {/* Tombol Submit dengan Spinner */}
        <button type="submit" className="btn" disabled={isLoading}>
          {isLoading ? <Spinner /> : 'Login'}
        </button>
      </form>

      <div className="auth-links split">
        <Link to="/forgot" className="auth-link">Lupa Password?</Link>
        <span>Belum punya akun? <Link to="/register" className="auth-link">Daftar di sini</Link></span>
      </div>
    </AuthCard>
  );
}

export default LoginPage;
